// SongLabAI - Google Maps Business Prospecting Configuration
// This node sets up the search parameters for California business discovery

const targetCities = [
  'Los Angeles', 'San Francisco', 'San Diego', 'Sacramento', 'Fresno',
  'Long Beach', 'Oakland', 'Bakersfield', 'Anaheim', 'Santa Ana',
  'Riverside', 'Stockton', 'Irvine', 'Chula Vista', 'Fremont',
  'San Bernardino', 'Modesto', 'Fontana', 'Oxnard', 'Moreno Valley',
  'Glendale', 'Huntington Beach', 'Santa Clarita', 'Garden Grove', 'Oceanside'
];

const businessTypes = [
  { type: 'car_dealer', keywords: 'car dealer', priority: 35 },
  { type: 'restaurant', keywords: 'restaurant', priority: 30 },
  { type: 'lawyer', keywords: 'lawyer', priority: 28 },
  { type: 'doctor', keywords: 'doctor', priority: 25 },
  { type: 'dentist', keywords: 'dentist', priority: 25 },
  { type: 'real_estate_agency', keywords: 'real estate', priority: 25 },
  { type: 'accounting', keywords: 'accountant', priority: 22 },
  { type: 'insurance_agency', keywords: 'insurance', priority: 20 }
];

const premiumAreas = [
  'beverly hills', 'santa monica', 'palo alto', 'carmel',
  'sausalito', 'napa', 'marin county', 'silicon valley',
  'la jolla', 'manhattan beach', 'newport beach', 'carmel-by-the-sea'
];

// Create search combinations (limit to stay within API quotas)
const searches = [];
const dailyLimit = parseInt(process.env.DAILY_GOOGLE_REQUESTS_LIMIT) || 300;
let searchCount = 0;

// Prioritize premium areas and high-value business types
for (const businessType of businessTypes) {
  for (const city of targetCities.slice(0, 10)) { // Focus on top 10 cities
    if (searchCount >= dailyLimit) break;

    const isPremiumArea = premiumAreas.some(area => 
      city.toLowerCase().includes(area) || 
      area.includes(city.toLowerCase())
    );

    searches.push({
      query: `${businessType.keywords} in ${city}, California`,
      city: city,
      businessType: businessType.type,
      priority: businessType.priority + (isPremiumArea ? 20 : 0),
      isPremiumArea: isPremiumArea
    });

    searchCount++;
  }
  if (searchCount >= dailyLimit) break;
}

// Sort by priority and return top searches
searches.sort((a, b) => b.priority - a.priority);

return searches.slice(0, Math.min(dailyLimit / 10, 30)).map((search, index) => ({
  json: {
    searchIndex: index,
    query: search.query,
    city: search.city,
    businessType: search.businessType,
    priority: search.priority,
    isPremiumArea: search.isPremiumArea,
    timestamp: new Date().toISOString()
  }
}));

// SongLabAI - Google Maps Results Processing
// Process and filter Google Places results for qualified prospects

const items = $input.all();
const qualifiedProspects = [];

// Premium California areas for higher scoring
const premiumAreas = [
  'beverly hills', 'santa monica', 'palo alto', 'carmel',
  'sausalito', 'napa', 'marin county', 'silicon valley',
  'la jolla', 'manhattan beach', 'newport beach', 'carmel-by-the-sea',
  'half moon bay', 'tiburon', 'mill valley', 'menlo park'
];

// Chain businesses to filter out (we want local businesses)
const chainKeywords = [
  'mcdonald', 'burger king', 'subway', 'starbucks', 'kfc', 'taco bell',
  'wendy', 'pizza hut', 'domino', 'papa john', 'ihop', 'denny',
  'applebee', 'chili', 'olive garden', 'red lobster', 'outback',
  'h&r block', 'jackson hewitt', 'liberty tax', 'geico', 'state farm'
];

for (const item of items) {
  const searchParams = item.json;
  const results = item.json.results || [];

  console.log(`Processing ${results.length} results for: ${searchParams.query}`);

  for (const business of results) {
    try {
      // Skip permanently closed businesses
      if (business.business_status === 'CLOSED_PERMANENTLY') {
        continue;
      }

      // Calculate prospect score
      let score = 0;

      // Base score by business type (from search parameters)
      const businessTypeScores = {
        'car_dealer': 35,
        'restaurant': 30,
        'lawyer': 28,
        'doctor': 25,
        'dentist': 25,
        'real_estate_agency': 25,
        'accounting': 22,
        'insurance_agency': 20
      };

      score += businessTypeScores[searchParams.businessType] || 20;

      // Premium location bonus
      const address = (business.formatted_address || '').toLowerCase();
      const inPremiumArea = premiumAreas.some(area => address.includes(area));
      if (inPremiumArea) {
        score += 20;
      }

      // Quality indicators
      if (business.rating && business.rating >= 4.0) {
        score += 15;
      } else if (business.rating && business.rating >= 3.5) {
        score += 10;
      }

      if (business.user_ratings_total) {
        if (business.user_ratings_total > 100) {
          score += 15;
        } else if (business.user_ratings_total > 50) {
          score += 10;
        } else if (business.user_ratings_total > 20) {
          score += 5;
        }
      }

      // Price level bonus (higher price = more budget potential)
      if (business.price_level) {
        if (business.price_level >= 3) {
          score += 15;
        } else if (business.price_level === 2) {
          score += 10;
        }
      }

      // Website presence bonus
      if (business.website) {
        score += 10;
      }

      // Phone number availability
      if (business.formatted_phone_number) {
        score += 5;
      }

      // Filter out chain businesses
      const businessName = (business.name || '').toLowerCase();
      const isChain = chainKeywords.some(chain => businessName.includes(chain));
      if (isChain) {
        score -= 25; // Heavy penalty for chains
      }

      // Only include high-scoring prospects
      const minScore = 45;
      if (score >= minScore) {
        // Determine specific business category
        const categories = business.types || [];
        let prospectType = searchParams.businessType;

        if (categories.includes('car_dealer')) prospectType = 'automotive';
        else if (categories.includes('restaurant') || categories.includes('meal_takeaway') || categories.includes('meal_delivery')) prospectType = 'restaurant';
        else if (categories.includes('lawyer') || categories.includes('legal_services')) prospectType = 'legal';
        else if (categories.includes('doctor') || categories.includes('hospital') || categories.includes('health')) prospectType = 'medical';
        else if (categories.includes('dentist') || categories.includes('dental_clinic')) prospectType = 'medical';
        else if (categories.includes('real_estate_agency')) prospectType = 'real_estate';

        // Generate personalized angle
        const personalizedAngle = generatePersonalizedAngle(business, inPremiumArea, prospectType);

        // Assess jingle needs based on business type
        const jingleNeeds = assessJingleNeeds(prospectType, categories);

        // Estimate budget
        const budgetEstimate = estimateBudget(business, inPremiumArea, prospectType);

        qualifiedProspects.push({
          // Basic Information
          name: business.name,
          business_name: business.name,
          address: business.formatted_address,
          phone: business.formatted_phone_number,
          website: business.website,

          // Classification
          prospect_type: prospectType,
          industry_category: categories.join(','),
          lead_source: 'google_maps',

          // Scoring and Priority
          lead_score: Math.min(score, 100),
          budget_estimate: budgetEstimate,
          priority_level: score >= 70 ? 'high' : (score >= 55 ? 'medium' : 'normal'),

          // Personalization
          jingle_needs: jingleNeeds,
          personalized_angle: personalizedAngle,
          pain_points: identifyPainPoints(prospectType),
          business_highlights: extractBusinessHighlights(business),

          // Geographic
          city: searchParams.city,
          state: 'CA',
          in_california: true,
          is_premium_area: inPremiumArea,
          latitude: business.geometry ? business.geometry.location.lat : null,
          longitude: business.geometry ? business.geometry.location.lng : null,

          // Google Data
          google_rating: business.rating,
          google_review_count: business.user_ratings_total || 0,
          price_level: business.price_level,

          // Google Maps Specific Data
          google_maps_data: {
            place_id: business.place_id,
            types: business.types,
            business_status: business.business_status,
            formatted_address: business.formatted_address,
            formatted_phone_number: business.formatted_phone_number,
            rating: business.rating,
            user_ratings_total: business.user_ratings_total,
            price_level: business.price_level,
            website: business.website
          },

          // Metadata
          search_query: searchParams.query,
          search_timestamp: new Date().toISOString(),
          tags: [prospectType, searchParams.city, inPremiumArea ? 'premium_area' : 'standard_area']
        });
      }
    } catch (error) {
      console.error('Error processing business:', business.name, error);
    }
  }
}

// Helper Functions
function generatePersonalizedAngle(business, isPremium, type) {
  const name = business.name;
  const rating = business.rating;

  if (isPremium) {
    return `${name} serves one of California's most discerning markets. A custom jingle would perfectly complement your premium brand positioning and help you stand out in this competitive area.`;
  }

  switch (type) {
    case 'automotive':
      return `California's competitive auto market means ${name} needs every advantage. A memorable jingle could be the difference-maker that makes customers think of you first when they're ready to buy.`;
    case 'restaurant':
      return `${name} has built a great reputation${rating ? ` with a ${rating}-star rating` : ''}. A custom jingle for your radio ads and social media could help you become the restaurant people can't stop humming about.`;
    case 'legal':
      return `${name} needs to be the first law firm potential clients think of in their time of need. A professional jingle creates instant brand recognition and trust.`;
    case 'medical':
      return `${name} provides essential healthcare services. A warm, professional jingle can help patients remember your practice and feel more comfortable choosing your care.`;
    case 'real_estate':
      return `In California's competitive real estate market, ${name} needs maximum brand recognition. A memorable jingle ensures you're the agent people call when they're ready to buy or sell.`;
    default:
      return `${name} has built a strong local presence. A custom jingle could take your brand recognition to the next level and help you dominate your local market.`;
  }
}

function assessJingleNeeds(type, categories) {
  const needs = ['brand_recognition', 'phone_hold'];

  switch (type) {
    case 'automotive':
      return [...needs, 'tv_commercials', 'radio_ads', 'showroom_music', 'video_content'];
    case 'restaurant':
      return [...needs, 'radio_ads', 'social_media', 'video_marketing', 'atmosphere_music'];
    case 'legal':
      return [...needs, 'tv_ads', 'radio_ads', 'video_content', 'professional_image'];
    case 'medical':
      return [...needs, 'waiting_room', 'radio_ads', 'video_marketing', 'patient_comfort'];
    case 'real_estate':
      return [...needs, 'radio_ads', 'video_marketing', 'social_media', 'property_videos'];
    default:
      return [...needs, 'marketing', 'social_media'];
  }
}

function estimateBudget(business, isPremium, type) {
  let budget = 'medium';

  // High-budget business types
  if (['automotive', 'legal'].includes(type)) {
    budget = 'high';
  }

  // Premium area adjustment
  if (isPremium) {
    if (budget === 'medium') budget = 'medium-high';
    if (budget === 'low') budget = 'medium';
  }

  // Price level adjustment
  if (business.price_level >= 3) {
    budget = 'high';
  } else if (business.price_level === 2 && budget === 'low') {
    budget = 'medium';
  }

  return budget;
}

function identifyPainPoints(type) {
  const common = ['brand_recognition', 'local_competition', 'marketing_effectiveness'];

  switch (type) {
    case 'automotive':
      return [...common, 'inventory_movement', 'customer_recall', 'seasonal_sales'];
    case 'restaurant':
      return [...common, 'customer_retention', 'peak_hour_optimization', 'social_media_presence'];
    case 'legal':
      return [...common, 'client_trust', 'professional_image', 'referral_generation'];
    case 'medical':
      return [...common, 'patient_comfort', 'appointment_scheduling', 'insurance_complexity'];
    case 'real_estate':
      return [...common, 'market_volatility', 'lead_generation', 'client_decision_time'];
    default:
      return common;
  }
}

function extractBusinessHighlights(business) {
  const highlights = [];

  if (business.rating >= 4.0) {
    highlights.push(`${business.rating}-star rating`);
  }

  if (business.user_ratings_total > 50) {
    highlights.push(`${business.user_ratings_total}+ customer reviews`);
  }

  if (business.price_level >= 2) {
    highlights.push('premium_pricing');
  }

  if (business.website) {
    highlights.push('online_presence');
  }

  return highlights;
}

// Sort by score and return top prospects
qualifiedProspects.sort((a, b) => b.lead_score - a.lead_score);

console.log(`Generated ${qualifiedProspects.length} qualified prospects`);

return qualifiedProspects.slice(0, 25).map(prospect => ({ json: prospect }));

// SongLabAI - Workflow Results Summary
// Generate summary statistics and log workflow execution

const items = $input.all();
const timestamp = new Date();

// Count results by type
const summary = {
  total_prospects: items.length,
  automotive: items.filter(item => item.json.prospect_type === 'automotive').length,
  restaurant: items.filter(item => item.json.prospect_type === 'restaurant').length,
  legal: items.filter(item => item.json.prospect_type === 'legal').length,
  medical: items.filter(item => item.json.prospect_type === 'medical').length,
  real_estate: items.filter(item => item.json.prospect_type === 'real_estate').length,
  premium_area_prospects: items.filter(item => item.json.is_premium_area).length,
  high_score_prospects: items.filter(item => item.json.lead_score >= 70).length,
  average_score: items.length > 0 ? Math.round(items.reduce((sum, item) => sum + item.json.lead_score, 0) / items.length) : 0,
  execution_timestamp: timestamp.toISOString(),
  workflow_name: 'Google Maps Business Prospecting'
};

// Log to console for monitoring
console.log('=== Google Maps Prospecting Summary ===');
console.log(`‚úÖ Total Prospects Added: ${summary.total_prospects}`);
console.log(`üöó Automotive: ${summary.automotive}`);
console.log(`üçΩÔ∏è Restaurant: ${summary.restaurant}`);
console.log(`‚öñÔ∏è Legal: ${summary.legal}`);
console.log(`üè• Medical: ${summary.medical}`);
console.log(`üè† Real Estate: ${summary.real_estate}`);
console.log(`üíé Premium Area: ${summary.premium_area_prospects}`);
console.log(`üéØ High Score (70+): ${summary.high_score_prospects}`);
console.log(`üìä Average Score: ${summary.average_score}`);
console.log('=========================================');

