// SongLabAI - Error Handler
// Log errors and send notifications for failed workflow execution

const error = $input.first();
const timestamp = new Date();

// Create error summary
const errorSummary = {
  workflow_name: 'Google Maps Business Prospecting',
  status: 'error',
  start_time: timestamp.toISOString(),
  end_time: timestamp.toISOString(),
  records_processed: 0,
  records_success: 0,
  error_message: error.error?.message || 'Unknown error occurred',
  error_stack: error.error?.stack || 'No stack trace available',
  context_data: {
    node_name: error.node?.name || 'Unknown node',
    error_type: error.error?.name || 'Unknown error type',
    execution_id: $executionId,
    timestamp: timestamp.toISOString()
  }
};

console.error('‚ùå Google Maps Prospecting Failed:', errorSummary);

return [{ json: errorSummary }];
