// SongLabAI - LinkedIn Alternative Search Configuration
// Configure search parameters for California business decision makers

const searchTargets = [
  // Automotive Decision Makers
  {
    keywords: 'car dealer owner manager California',
    industry: 'automotive',
    titles: ['Owner', 'General Manager', 'Sales Manager', 'Marketing Director'],
    priority: 35,
    type: 'automotive'
  },

  // Restaurant Decision Makers
  {
    keywords: 'restaurant owner manager California',
    industry: 'restaurants',
    titles: ['Owner', 'Manager', 'Marketing Manager', 'Brand Manager'],
    priority: 30,
    type: 'restaurant'
  },

  // Legal Decision Makers
  {
    keywords: 'law firm partner attorney California',
    industry: 'legal services',
    titles: ['Partner', 'Managing Partner', 'Marketing Director', 'Business Development'],
    priority: 28,
    type: 'legal'
  },

  // Medical Decision Makers
  {
    keywords: 'medical practice doctor dentist California',
    industry: 'medical practice',
    titles: ['Doctor', 'Practice Manager', 'Medical Director', 'Administrator'],
    priority: 25,
    type: 'medical'
  },

  // Real Estate Decision Makers
  {
    keywords: 'real estate broker agent California',
    industry: 'real estate',
    titles: ['Broker', 'Owner', 'Team Leader', 'Marketing Director'],
    priority: 25,
    type: 'real_estate'
  }
];

const californiaLocations = [
  'Los Angeles, CA', 'San Francisco, CA', 'San Diego, CA', 'Sacramento, CA',
  'Fresno, CA', 'Long Beach, CA', 'Oakland, CA', 'Bakersfield, CA',
  'Anaheim, CA', 'Santa Ana, CA', 'Riverside, CA', 'Stockton, CA'
];

// Create search combinations
const searches = [];
const dailyLimit = parseInt(process.env.DAILY_LINKEDIN_REQUESTS_LIMIT) || 100;
let searchCount = 0;

for (const target of searchTargets) {
  for (let i = 0; i < Math.min(californiaLocations.length, 3); i++) {
    if (searchCount >= dailyLimit) break;

    const location = californiaLocations[i];

    searches.push({
      searchIndex: searchCount,
      keywords: target.keywords,
      location: location,
      industry: target.industry,
      titles: target.titles,
      priority: target.priority,
      prospect_type: target.type,
      search_type: 'company_search',
      timestamp: new Date().toISOString()
    });

    searchCount++;
  }
  if (searchCount >= dailyLimit) break;
}

// Sort by priority
searches.sort((a, b) => b.priority - a.priority);

console.log(`Generated ${searches.length} LinkedIn alternative searches`);

return searches.slice(0, Math.min(dailyLimit / 5, 20)).map(search => ({ json: search }));

