// Process and qualify LinkedIn alternative API results

const items = $input.all();
const qualifiedProspects = [];

// Email patterns to extract from profile descriptions or company data
const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;

for (const item of items) {
  try {
    const searchParams = item.json;
    let results = [];

    // Handle different API response formats
    if (item.json.employees) {
      results = item.json.employees; // Proxycurl format
    } else if (item.json.data) {
      results = item.json.data; // RapidAPI format
    } else if (Array.isArray(item.json)) {
      results = item.json; // Direct array format
    }

    console.log(`Processing ${results.length} LinkedIn results for: ${searchParams.keywords}`);

    for (const profile of results) {
      try {
        // Calculate prospect score
        let score = 0;

        // Base score by prospect type
        const baseScores = {
          'automotive': 35,
          'restaurant': 30,
          'legal': 28,
          'medical': 25,
          'real_estate': 25
        };

        score += baseScores[searchParams.prospect_type] || 20;

        // Position/title bonus
        const title = (profile.headline || profile.job_title || '').toLowerCase();
        const seniorTitles = ['owner', 'partner', 'ceo', 'president', 'director', 'manager'];
        if (seniorTitles.some(seniorTitle => title.includes(seniorTitle))) {
          score += 25;
        }

        // Experience bonus
        if (profile.experience_years && profile.experience_years > 5) {
          score += 15;
        } else if (profile.experience_years && profile.experience_years > 2) {
          score += 10;
        }

        // Location bonus (California)
        const location = (profile.location || searchParams.location || '').toLowerCase();
        if (location.includes('california') || location.includes('ca')) {
          score += 10;
        }

        // Premium area bonus
        const premiumAreas = ['los angeles', 'san francisco', 'san diego', 'palo alto', 'silicon valley'];
        const inPremiumArea = premiumAreas.some(area => location.includes(area));
        if (inPremiumArea) {
          score += 15;
        }

        // Company size bonus
        if (profile.company_size) {
          const companySize = profile.company_size.toLowerCase();
          if (companySize.includes('51-200') || companySize.includes('11-50')) {
            score += 10; // Sweet spot for our services
          }
        }

        // LinkedIn activity bonus
        if (profile.connections && profile.connections > 500) {
          score += 10;
        }

        // Only include prospects with minimum score
        if (score >= 40) {
          // Extract or generate email
          let email = null;

          // Try to extract email from profile
          const profileText = [
            profile.summary || '',
            profile.headline || '',
            profile.company_description || ''
          ].join(' ');

          const emailMatches = profileText.match(emailRegex);
          if (emailMatches && emailMatches.length > 0) {
            email = emailMatches[0];
          } else {
            // Generate likely email patterns
            const firstName = (profile.first_name || '').toLowerCase();
            const lastName = (profile.last_name || '').toLowerCase();
            const companyName = (profile.current_company || '').toLowerCase().replace(/[^a-z0-9]/g, '');

            if (firstName && lastName && companyName) {
              // Common email patterns
              const patterns = [
                `${firstName}.${lastName}@${companyName}.com`,
                `${firstName}@${companyName}.com`,
                `${firstName}${lastName}@${companyName}.com`
              ];
              email = patterns[0]; // Use first pattern as best guess
            }
          }

          // Generate personalized angle
          const personalizedAngle = generateLinkedInPersonalizedAngle(profile, searchParams);

          qualifiedProspects.push({
            // Basic Information
            name: `${profile.first_name || ''} ${profile.last_name || ''}`.trim() || profile.name,
            business_name: profile.current_company || profile.company_name,
            email: email,

            // Classification
            prospect_type: searchParams.prospect_type,
            industry_category: profile.industry || searchParams.industry,
            lead_source: 'linkedin_alternative',

            // Scoring
            lead_score: Math.min(score, 100),
            budget_estimate: estimateLinkedInBudget(profile, searchParams.prospect_type),
            priority_level: score >= 70 ? 'high' : (score >= 55 ? 'medium' : 'normal'),

            // Personalization
            jingle_needs: assessLinkedInJingleNeeds(searchParams.prospect_type),
            personalized_angle: personalizedAngle,
            pain_points: identifyLinkedInPainPoints(searchParams.prospect_type),

            // Geographic
            city: extractCity(profile.location || searchParams.location),
            state: 'CA',
            in_california: true,
            is_premium_area: inPremiumArea,

            // LinkedIn Specific Data
            linkedin_data: {
              linkedin_url: profile.linkedin_url || profile.profile_url,
              headline: profile.headline,
              current_position: profile.job_title || profile.current_position,
              current_company: profile.current_company,
              industry: profile.industry,
              location: profile.location,
              connections_count: profile.connections,
              experience_years: profile.experience_years,
              data_source: item.json.employees ? 'proxycurl' : 'rapidapi',
              api_response: profile
            },

            // Metadata
            search_query: searchParams.keywords,
            search_timestamp: new Date().toISOString(),
            tags: [searchParams.prospect_type, 'linkedin_alternative', inPremiumArea ? 'premium_area' : 'standard_area']
          });
        }
      } catch (profileError) {
        console.error('Error processing LinkedIn profile:', profileError);
      }
    }
  } catch (itemError) {
    console.error('Error processing LinkedIn search result:', itemError);
  }
}

// Helper Functions
function generateLinkedInPersonalizedAngle(profile, searchParams) {
  const name = profile.first_name || 'there';
  const company = profile.current_company || 'your business';
  const title = profile.headline || profile.job_title || 'business professional';

  const angles = {
    automotive: `Hi ${name}, I noticed your role as ${title} at ${company}. California's auto market is incredibly competitive - a custom jingle could give you the brand recognition edge that drives more customers to your lot.`,
    restaurant: `Hi ${name}, your experience as ${title} at ${company} caught my attention. In California's crowded restaurant scene, a memorable jingle could be what makes customers choose you over the competition.`,
    legal: `Dear ${name}, as ${title} at ${company}, you understand the importance of being top-of-mind when people need legal services. A professional jingle ensures your firm is the first one clients remember.`,
    medical: `Hi ${name}, your role as ${title} at ${company} shows your commitment to patient care. A soothing, professional jingle can help reduce patient anxiety and build trust in your practice.`,
    real_estate: `Hi ${name}, as ${title} at ${company}, you know how crucial it is to be the agent people remember. A custom jingle ensures you're top-of-mind when they're ready to buy or sell.`
  };

  return angles[searchParams.prospect_type] || `Hi ${name}, your role as ${title} at ${company} caught my attention. A custom jingle could help your business stand out in California's competitive market.`;
}

function estimateLinkedInBudget(profile, type) {
  let budget = 'medium';

  // High-budget business types
  if (['automotive', 'legal'].includes(type)) {
    budget = 'high';
  }

  // Senior position adjustment
  const title = (profile.headline || profile.job_title || '').toLowerCase();
  if (title.includes('owner') || title.includes('partner') || title.includes('ceo')) {
    budget = budget === 'medium' ? 'medium-high' : 'high';
  }

  return budget;
}

function assessLinkedInJingleNeeds(type) {
  const common = ['brand_recognition', 'professional_image'];

  const specific = {
    automotive: [...common, 'tv_commercials', 'radio_ads', 'showroom_music'],
    restaurant: [...common, 'radio_ads', 'social_media', 'atmosphere_music'],
    legal: [...common, 'tv_ads', 'phone_hold', 'professional_image'],
    medical: [...common, 'waiting_room', 'phone_hold', 'patient_comfort'],
    real_estate: [...common, 'radio_ads', 'video_marketing', 'social_media']
  };

  return specific[type] || common;
}

function identifyLinkedInPainPoints(type) {
  const common = ['brand_recognition', 'local_competition', 'lead_generation'];

  const specific = {
    automotive: [...common, 'inventory_movement', 'seasonal_sales'],
    restaurant: [...common, 'customer_retention', 'social_media_presence'],
    legal: [...common, 'client_trust', 'referral_generation'],
    medical: [...common, 'patient_comfort', 'appointment_scheduling'],
    real_estate: [...common, 'market_volatility', 'client_decision_time']
  };

  return specific[type] || common;
}

function extractCity(location) {
  if (!location) return 'Los Angeles'; // Default

  const cityMatch = location.match(/([^,]+),?\s*CA/i);
  return cityMatch ? cityMatch[1].trim() : location.split(',')[0].trim();
}

// Sort by score and return top prospects
qualifiedProspects.sort((a, b) => b.lead_score - a.lead_score);

console.log(`Generated ${qualifiedProspects.length} qualified LinkedIn prospects`);

return qualifiedProspects.slice(0, 15).map(prospect => ({ json: prospect }));"
