// SongLabAI - Analytics Dashboard Generator
// Combines all analytics data into comprehensive dashboard

const items = $input.all();
const timestamp = new Date();

// Extract analytics data from each query
const prospectAnalytics = items[0]?.json || {};
const emailAnalytics = items[1]?.json || {};
const meetingAnalytics = items[2]?.json || {};
const workflowAnalytics = items.slice(3) || [];

// Generate comprehensive dashboard
const dashboard = {
  // Report Metadata
  report_generated: timestamp.toISOString(),
  report_type: 'analytics_dashboard',
  period: '6_hourly',

  // Prospect Performance
  prospects: {
    today: parseInt(prospectAnalytics.prospects_today) || 0,
    week: parseInt(prospectAnalytics.prospects_7days) || 0,
    month: parseInt(prospectAnalytics.prospects_30days) || 0,

    sources: {
      google_maps_today: parseInt(prospectAnalytics.google_prospects_today) || 0,
      linkedin_today: parseInt(prospectAnalytics.linkedin_prospects_today) || 0
    },

    types: {
      automotive_today: parseInt(prospectAnalytics.automotive_today) || 0,
      restaurant_today: parseInt(prospectAnalytics.restaurant_today) || 0,
      legal_today: parseInt(prospectAnalytics.legal_today) || 0,
      medical_today: parseInt(prospectAnalytics.medical_today) || 0
    },

    quality: {
      avg_score_today: Math.round(parseFloat(prospectAnalytics.avg_score_today) || 0),
      high_score_today: parseInt(prospectAnalytics.high_score_today) || 0,
      premium_area_today: parseInt(prospectAnalytics.premium_area_today) || 0
    },

    pipeline: {
      new: parseInt(prospectAnalytics.new_prospects) || 0,
      contacted: parseInt(prospectAnalytics.contacted_prospects) || 0,
      meetings_scheduled: parseInt(prospectAnalytics.meeting_scheduled) || 0,
      total_contacted: parseInt(prospectAnalytics.total_contacted) || 0,
      total_meetings_booked: parseInt(prospectAnalytics.total_meetings_booked) || 0
    }
  },

  // Email Performance
  emails: {
    today: {
      sent: parseInt(emailAnalytics.emails_sent_today) || 0,
      opened: parseInt(emailAnalytics.emails_opened_today) || 0,
      clicked: parseInt(emailAnalytics.emails_clicked_today) || 0,
      replied: parseInt(emailAnalytics.emails_replied_today) || 0
    },

    week: {
      sent: parseInt(emailAnalytics.emails_sent_7days) || 0,
      opened: parseInt(emailAnalytics.emails_opened_7days) || 0,
      clicked: parseInt(emailAnalytics.emails_clicked_7days) || 0,
      replied: parseInt(emailAnalytics.emails_replied_7days) || 0,
      initial_outreach: parseInt(emailAnalytics.initial_outreach_7days) || 0,
      follow_ups: parseInt(emailAnalytics.follow_up_1_7days) || 0,
      confirmations: parseInt(emailAnalytics.confirmations_7days) || 0
    },

    performance: {
      open_rate: parseFloat(emailAnalytics.open_rate_30days) || 0,
      click_rate: parseFloat(emailAnalytics.click_rate_30days) || 0,
      reply_rate: parseFloat(emailAnalytics.reply_rate_30days) || 0
    }
  },

  // Meeting Performance
  meetings: {
    today: {
      meetings: parseInt(meetingAnalytics.meetings_today) || 0,
      bookings: parseInt(meetingAnalytics.bookings_today) || 0
    },

    week: {
      meetings: parseInt(meetingAnalytics.meetings_7days) || 0,
      bookings: parseInt(meetingAnalytics.bookings_7days) || 0,
      automotive: parseInt(meetingAnalytics.automotive_meetings_7days) || 0,
      restaurant: parseInt(meetingAnalytics.restaurant_meetings_7days) || 0,
      legal: parseInt(meetingAnalytics.legal_meetings_7days) || 0,
      medical: parseInt(meetingAnalytics.medical_meetings_7days) || 0,
      general: parseInt(meetingAnalytics.general_meetings_7days) || 0
    },

    status: {
      scheduled: parseInt(meetingAnalytics.scheduled_meetings) || 0,
      completed: parseInt(meetingAnalytics.completed_meetings) || 0,
      canceled: parseInt(meetingAnalytics.canceled_meetings) || 0,
      no_show: parseInt(meetingAnalytics.no_show_meetings) || 0
    },

    matching: {
      with_prospects: parseInt(meetingAnalytics.meetings_with_prospects) || 0,
      without_prospects: parseInt(meetingAnalytics.meetings_without_prospects) || 0,
      match_rate: Math.round((parseInt(meetingAnalytics.meetings_with_prospects) || 0) * 100 / Math.max((parseInt(meetingAnalytics.meetings_with_prospects) || 0) + (parseInt(meetingAnalytics.meetings_without_prospects) || 0), 1))
    },

    upcoming: parseInt(meetingAnalytics.upcoming_meetings) || 0
  },

  // Workflow Health
  workflows: workflowAnalytics.map(workflow => ({
    name: workflow.json.workflow_name,
    executions_today: parseInt(workflow.json.executions_today) || 0,
    executions_week: parseInt(workflow.json.executions_7days) || 0,
    success_rate: parseFloat(workflow.json.success_rate) || 0,
    avg_duration: Math.round(parseFloat(workflow.json.avg_duration_seconds) || 0),
    total_records: parseInt(workflow.json.total_records_processed) || 0,
    last_execution: workflow.json.last_execution,
    errors_24h: parseInt(workflow.json.errors_24h) || 0,
    health_status: determineWorkflowHealth(workflow.json)
  })),

  // Key Performance Indicators
  kpis: {
    // Conversion Funnel
    prospect_to_contact_rate: Math.round((parseInt(prospectAnalytics.total_contacted) || 0) * 100 / Math.max(parseInt(prospectAnalytics.prospects_30days) || 1, 1)),
    contact_to_meeting_rate: Math.round((parseInt(prospectAnalytics.total_meetings_booked) || 0) * 100 / Math.max(parseInt(prospectAnalytics.total_contacted) || 1, 1)),

    // Daily Performance
    daily_prospect_velocity: (parseInt(prospectAnalytics.prospects_7days) || 0) / 7,
    daily_email_velocity: (parseInt(emailAnalytics.emails_sent_7days) || 0) / 7,
    daily_meeting_velocity: (parseInt(meetingAnalytics.bookings_7days) || 0) / 7,

    // Quality Metrics
    avg_lead_score: Math.round(parseFloat(prospectAnalytics.avg_score_today) || 0),
    premium_area_percentage: Math.round((parseInt(prospectAnalytics.premium_area_today) || 0) * 100 / Math.max(parseInt(prospectAnalytics.prospects_today) || 1, 1)),

    // System Health
    overall_system_health: calculateOverallHealth(workflowAnalytics)
  },

  // Alerts and Recommendations
  alerts: generateAlerts(prospectAnalytics, emailAnalytics, meetingAnalytics, workflowAnalytics),

  // Next Actions
  recommendations: generateRecommendations(prospectAnalytics, emailAnalytics, meetingAnalytics)
};

// Helper Functions
function determineWorkflowHealth(workflow) {
  const successRate = parseFloat(workflow.success_rate) || 0;
  const errors24h = parseInt(workflow.errors_24h) || 0;
  const executionsToday = parseInt(workflow.executions_today) || 0;

  if (errors24h > 0) return 'error';
  if (successRate < 80) return 'warning';
  if (executionsToday === 0 && workflow.workflow_name !== 'LinkedIn Alternative Prospecting') return 'warning';
  return 'healthy';
}

function calculateOverallHealth(workflows) {
  if (workflows.length === 0) return 'unknown';

  const healthStatuses = workflows.map(w => determineWorkflowHealth(w.json));
  const errorCount = healthStatuses.filter(s => s === 'error').length;
  const warningCount = healthStatuses.filter(s => s === 'warning').length;

  if (errorCount > 0) return 'critical';
  if (warningCount > 1) return 'warning';
  return 'healthy';
}

function generateAlerts(prospects, emails, meetings, workflows) {
  const alerts = [];

  // Prospect Generation Alerts
  if ((parseInt(prospects.prospects_today) || 0) < 10) {
    alerts.push({
      type: 'warning',
      category: 'prospects',
      message: `Low prospect generation today: ${prospects.prospects_today || 0} prospects`,
      action: 'Check Google Maps API quotas and workflow execution'
    });
  }

  // Email Performance Alerts
  const openRate = parseFloat(emails.open_rate_30days) || 0;
  if (openRate < 15) {
    alerts.push({
      type: 'warning',
      category: 'emails',
      message: `Low email open rate: ${openRate}%`,
      action: 'Review email subject lines and sending reputation'
    });
  }

  // Meeting Booking Alerts
  if ((parseInt(meetings.bookings_7days) || 0) < 3) {
    alerts.push({
      type: 'info',
      category: 'meetings',
      message: `Low meeting bookings this week: ${meetings.bookings_7days || 0} bookings`,
      action: 'Review email CTAs and Calendly link placement'
    });
  }

  // Workflow Health Alerts
  workflows.forEach(workflow => {
    const health = determineWorkflowHealth(workflow.json);
    if (health === 'error') {
      alerts.push({
        type: 'error',
        category: 'workflow',
        message: `${workflow.json.workflow_name} has errors in the last 24h`,
        action: 'Check workflow logs and fix underlying issues'
      });
    }
  });

  return alerts;
}

function generateRecommendations(prospects, emails, meetings) {
  const recommendations = [];

  // Prospect Quality Recommendations
  const avgScore = parseFloat(prospects.avg_score_today) || 0;
  if (avgScore < 60) {
    recommendations.push({
      category: 'prospects',
      priority: 'medium',
      message: 'Consider adjusting lead scoring criteria to focus on higher-quality prospects',
      impact: 'Higher conversion rates'
    });
  }

  // Email Performance Recommendations
  const replyRate = parseFloat(emails.reply_rate_30days) || 0;
  if (replyRate < 5) {
    recommendations.push({
      category: 'emails',
      priority: 'high',
      message: 'Low reply rate suggests need for more personalized email content',
      impact: 'Increased engagement and meeting bookings'
    });
  }

  // Meeting Optimization Recommendations
  const weeklyMeetings = parseInt(meetings.bookings_7days) || 0;
  const weeklyEmails = parseInt(emails.emails_sent_7days) || 0;
  const emailToMeetingRate = weeklyEmails > 0 ? (weeklyMeetings / weeklyEmails) * 100 : 0;

  if (emailToMeetingRate < 10) {
    recommendations.push({
      category: 'conversion',
      priority: 'high',
      message: 'Email-to-meeting conversion rate is low. Consider A/B testing CTAs and offering stronger incentives',
      impact: 'More meetings booked from same email volume'
    });
  }

  return recommendations;
}

// Log comprehensive dashboard to console
console.log('=== SongLabAI Analytics Dashboard ===');
console.log(`ðŸ“Š Generated at: ${dashboard.report_generated}`);
console.log(`\nðŸŽ¯ PROSPECTS:`);
console.log(`  Today: ${dashboard.prospects.today} | Week: ${dashboard.prospects.week} | Month: ${dashboard.prospects.month}`);
console.log(`  Avg Score: ${dashboard.kpis.avg_lead_score} | High Score: ${dashboard.prospects.quality.high_score_today}`);
console.log(`\nðŸ“§ EMAILS:`);
console.log(`  Sent Today: ${dashboard.emails.today.sent} | Week: ${dashboard.emails.week.sent}`);
console.log(`  Performance: ${dashboard.emails.performance.open_rate}% open, ${dashboard.emails.performance.click_rate}% click, ${dashboard.emails.performance.reply_rate}% reply`);
console.log(`\nðŸ“… MEETINGS:`);
console.log(`  Booked Today: ${dashboard.meetings.today.bookings} | Week: ${dashboard.meetings.week.bookings}`);
console.log(`  Scheduled: ${dashboard.meetings.status.scheduled} | Completed: ${dashboard.meetings.status.completed}`);
console.log(`\nâš¡ SYSTEM HEALTH: ${dashboard.kpis.overall_system_health.toUpperCase()}`);
console.log(`\nðŸš¨ ALERTS: ${dashboard.alerts.length} active`);
dashboard.alerts.forEach(alert => console.log(`  ${alert.type.toUpperCase()}: ${alert.message}`));
console.log('=====================================');

return [{ json: dashboard }];

