// SongLabAI - Health Check & System Validation
// Perform system health checks and validate all components

const dashboard = $input.first().json;
const timestamp = new Date();

// System Health Checks
const healthChecks = {
  timestamp: timestamp.toISOString(),

  // Database Health
  database: {
    status: 'healthy', // Will be updated based on query success
    last_prospect: dashboard.prospects.today > 0 ? 'recent' : 'stale',
    data_freshness: dashboard.prospects.today > 0 ? 'fresh' : 'needs_attention'
  },

  // API Health (based on recent activity)
  apis: {
    google_maps: {
      status: dashboard.prospects.sources.google_maps_today > 0 ? 'active' : 'inactive',
      daily_usage: dashboard.prospects.sources.google_maps_today,
      quota_status: dashboard.prospects.sources.google_maps_today < 250 ? 'healthy' : 'approaching_limit'
    },

    calendly: {
      status: dashboard.meetings.today.bookings >= 0 ? 'active' : 'inactive',
      recent_bookings: dashboard.meetings.today.bookings,
      integration_health: dashboard.meetings.matching.match_rate > 60 ? 'healthy' : 'needs_review'
    },

    smtp: {
      status: dashboard.emails.today.sent > 0 ? 'active' : 'inactive',
      daily_usage: dashboard.emails.today.sent,
      performance: dashboard.emails.performance.open_rate > 15 ? 'healthy' : 'poor'
    }
  },

  // Workflow Health
  workflows: dashboard.workflows.map(workflow => ({
    name: workflow.name,
    status: workflow.health_status,
    last_run: workflow.last_execution,
    errors_24h: workflow.errors_24h,
    recommendation: getWorkflowRecommendation(workflow)
  })),

  // Performance Indicators
  performance: {
    prospect_generation_rate: dashboard.kpis.daily_prospect_velocity,
    email_send_rate: dashboard.kpis.daily_email_velocity,
    meeting_booking_rate: dashboard.kpis.daily_meeting_velocity,
    overall_conversion_rate: dashboard.kpis.contact_to_meeting_rate,
    system_efficiency: calculateSystemEfficiency(dashboard)
  },

  // Resource Usage
  resources: {
    daily_api_calls_estimated: estimateApiUsage(dashboard),
    database_growth_rate: estimateDatabaseGrowth(dashboard),
    email_quota_usage: Math.round((dashboard.emails.today.sent / 250) * 100), // Assuming 250 daily limit
    storage_health: 'monitoring_needed' // Would need actual storage metrics
  },

  // Overall System Score
  overall_health_score: calculateHealthScore(dashboard),

  // Next Maintenance Actions
  maintenance_needed: generateMaintenanceActions(dashboard)
};

// Helper Functions
function getWorkflowRecommendation(workflow) {
  if (workflow.errors_24h > 0) return 'immediate_attention_required';
  if (workflow.success_rate < 90) return 'investigate_failures';
  if (workflow.avg_duration > 300) return 'optimize_performance';
  return 'healthy';
}

function calculateSystemEfficiency(dashboard) {
  const prospectEfficiency = Math.min(dashboard.prospects.today / 30, 1); // Target: 30/day
  const emailEfficiency = Math.min(dashboard.emails.today.sent / 25, 1); // Target: 25/day
  const meetingEfficiency = Math.min(dashboard.meetings.today.bookings / 3, 1); // Target: 3/day

  return Math.round((prospectEfficiency + emailEfficiency + meetingEfficiency) / 3 * 100);
}

function estimateApiUsage(dashboard) {
  return {
    google_maps: dashboard.prospects.sources.google_maps_today * 10, // Estimate multiple calls per prospect
    calendly: 96, // 15-minute polling = 96 calls/day
    linkedin: Math.floor(dashboard.prospects.sources.linkedin_today * 1.5), // Estimate
    total_estimated: (dashboard.prospects.sources.google_maps_today * 10) + 96 + Math.floor(dashboard.prospects.sources.linkedin_today * 1.5)
  };
}

function estimateDatabaseGrowth(dashboard) {
  const dailyRows = dashboard.prospects.today + dashboard.emails.today.sent + dashboard.meetings.today.bookings;
  return {
    rows_added_today: dailyRows,
    estimated_monthly_growth: dailyRows * 30,
    storage_impact: 'low' // Most rows are small
  };
}

function calculateHealthScore(dashboard) {
  let score = 100;

  // Deduct for system issues
  if (dashboard.kpis.overall_system_health === 'critical') score -= 30;
  else if (dashboard.kpis.overall_system_health === 'warning') score -= 15;

  // Deduct for low performance
  if (dashboard.emails.performance.open_rate < 15) score -= 10;
  if (dashboard.kpis.contact_to_meeting_rate < 10) score -= 10;
  if (dashboard.prospects.today < 10) score -= 10;

  // Deduct for active alerts
  score -= dashboard.alerts.length * 5;

  return Math.max(score, 0);
}

function generateMaintenanceActions(dashboard) {
  const actions = [];

  if (dashboard.kpis.overall_system_health === 'critical') {
    actions.push({
      priority: 'high',
      action: 'Fix critical workflow errors immediately',
      timeline: 'within_2_hours'
    });
  }

  if (dashboard.emails.performance.open_rate < 15) {
    actions.push({
      priority: 'medium',
      action: 'Review and improve email templates and subject lines',
      timeline: 'within_24_hours'
    });
  }

  if (dashboard.prospects.today < 10) {
    actions.push({
      priority: 'medium',
      action: 'Check Google Maps API quotas and search parameters',
      timeline: 'within_24_hours'
    });
  }

  // Regular maintenance
  actions.push({
    priority: 'low',
    action: 'Archive old workflow logs and optimize database',
    timeline: 'weekly'
  });

  return actions;
}

// Log health check results
console.log('=== System Health Check ===');
console.log(`Overall Health Score: ${healthChecks.overall_health_score}/100`);
console.log(`System Efficiency: ${healthChecks.performance.system_efficiency}%`);
console.log(`API Usage Today: ${healthChecks.resources.daily_api_calls_estimated.total_estimated} calls`);
console.log(`Maintenance Actions: ${healthChecks.maintenance_needed.length} required`);
console.log('==========================');

return [{ json: healthChecks }];
